	UPDATE	BudgetItem
	SET		Amount = 	(
							SELECT	(SUM(TakeHomePay) * 26) / 12 AS Amount
							FROM	BudgetIncome BudgetIncome
							WHERE	BudgetIncome.BudgetNumber = BudgetNumber
