        ,BudgetMonth
        ,CreateDT
        ,CreateBy
    )
    SELECT	BudgetNumber
			,BudgetMonth
            ,NOW()
            ,'User'
	FROM DUAL
	WHERE NOT EXISTS (SELECT Budget.BudgetMonth FROM Budget Budget WHERE TIMESTAMPDIFF(MONTH, Budget.BudgetMonth, BudgetMonth) = 0)
    ;

    SET BudgetIDNew = LAST_INSERT_ID();
    
    
	INSERT INTO BudgetItem
    (
