            ,BudgetItem.BudgetCategoryID
            ,BudgetItem.BudgetTypeID
            ,BudgetItem.Amount
            ,BudgetItem.Sort
            ,NOW()
            ,'User'
    FROM 	BudgetItem BudgetItem
    WHERE	BudgetItem.BudgetID = BudgetIDPrevious 
    AND 	BudgetIDPrevious IS NOT NULL
    AND		BudgetIDNew <> 0;
    
    
END ;;
